function maskPII(obj) {
  if (!obj || typeof obj !== 'object') return obj;
  const piiKeys = ['email', 'customer_email', 'buyer_email', 'name', 'customer_name', 'phone', 'address'];
  const masked = { ...obj };
  for (const key of Object.keys(masked)) {
    if (piiKeys.some(p => key.toLowerCase().includes(p))) {
      masked[key] = typeof masked[key] === 'string' ? '***' : masked[key];
    } else if (typeof masked[key] === 'object') {
      masked[key] = maskPII(masked[key]);
    }
  }
  return masked;
}

