const body = $input.first().json.body || $input.first().json || {};

const targetUrl = body.target_url;
const actions = body.actions || []; // [{type: 'click', selector: '#btn'}, {type: 'wait', ms: 2000}]
const resolution = body.resolution || '1920x1080';
const fps = body.fps || 30;
const durationLimit = body.duration_limit_seconds || 300;
const channelId = body.channel_id;
const contentId = body.content_id;

if (!targetUrl) {
  return [{ json: { error: true, message: 'target_url ã¯å¿…é ˆã§ã™', code: 'MISSING_PARAM' } }];
}

const jobId = 'rec_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);

return [{
  json: {
    job_id: jobId,
    target_url: targetUrl,
    actions: actions,
