// ============================================================================
// Puppeteerã‚¹ã‚¯ãƒªãƒ—ãƒˆç”Ÿæˆ
// VPSä¸Šã§å®Ÿè¡Œã™ã‚‹ãŸã‚ã®Node.jsã‚¹ã‚¯ãƒªãƒ—ãƒˆã‚’ç”Ÿæˆ
// ============================================================================

const config = $node['ğŸ”§ åˆæœŸåŒ–'].json;
const [width, height] = config.resolution.split('x').map(Number);

const puppeteerScript = `
const puppeteer = require('puppeteer');
const { PuppeteerScreenRecorder } = require('puppeteer-screen-recorder');
const path = require('path');

(async () => {
  const browser = await puppeteer.launch({
    headless: 'new',
    args: ['--no-sandbox', '--disable-setuid-sandbox', '--window-size=${width},${height}']
  });

  const page = await browser.newPage();
  await page.setViewport({ width: ${width}, height: ${height} });

  const outputPath = '/tmp/recordings/${config.job_id}.mp4';

  const recorder = new PuppeteerScreenRecorder(page, {
    fps: ${config.fps},
    ffmpeg_Path: '/usr/bin/ffmpeg',
    videoFrame: { width: ${width}, height: ${height} },
    aspectRatio: '16:9'
  });

  await page.goto('${config.target_url}', { waitUntil: 'networkidle2', timeout: 60000 });

  await recorder.start(outputPath);

  // ã‚¢ã‚¯ã‚·ãƒ§ãƒ³å®Ÿè¡Œ
  const actions = ${JSON.stringify(config.actions)};
  for (const action of actions) {
    try {
      switch (action.type) {
        case 'click':
          await page.click(action.selector);
          break;
        case 'type':
          await page.type(action.selector, action.text);
          break;
        case 'scroll':
          await page.evaluate((amount) => window.scrollBy(0, amount), action.amount || 500);
          break;
        case 'wait':
          await new Promise(r => setTimeout(r, action.ms || 1000));
          break;
        case 'screenshot':
          await page.screenshot({ path: action.path || '/tmp/screenshot.png' });
          break;
      }
      await new Promise(r => setTimeout(r, 500)); // ã‚¢ã‚¯ã‚·ãƒ§ãƒ³é–“ã®é–“éš”
    } catch (e) {
      console.error('Action failed:', action, e.message);
    }
  }

  // æœ€å¤§éŒ²ç”»æ™‚é–“ã¾ã§å¾…æ©Ÿï¼ˆã¾ãŸã¯æ—©æœŸçµ‚äº†ï¼‰
  const maxDuration = Math.min(${config.duration_limit} * 1000, 300000);
  const startTime = Date.now();
  while (Date.now() - startTime < maxDuration) {
    await new Promise(r => setTimeout(r, 1000));
  }

  await recorder.stop();
  await browser.close();

  console.log(JSON.stringify({
    success: true,
    output_path: outputPath,
    job_id: '${config.job_id}',
    duration_seconds: (Date.now() - startTime) / 1000
  }));
})();
`;

return [{
  json: {
    ...config,
    puppeteer_script: puppeteerScript,
    output_path: `/tmp/recordings/${config.job_id}.mp4`
  }
}];

