const config = $node['ğŸ“ ã‚¹ã‚¯ãƒªãƒ—ãƒˆç”Ÿæˆ'].json;
const execResult = $input.first().json;

let success = false;
let outputPath = config.output_path;
let durationSeconds = 0;
let errorMessage = null;

try {
  if (execResult.stdout) {
    const result = JSON.parse(execResult.stdout.trim());
    success = result.success;
    outputPath = result.output_path;
    durationSeconds = result.duration_seconds;
  }
} catch (e) {
  errorMessage = execResult.stderr || e.message;
}

return [{
  json: {
    job_id: config.job_id,
    success: success,
    output_path: outputPath,
    duration_seconds: durationSeconds,
    error_message: errorMessage,
    channel_id: config.channel_id,
    content_id: config.content_id
  }
}];

const data = $node['ğŸ“‹ çµæœè§£æ'].json;
const supabaseUrl = $env.SUPABASE_URL || '{{$env.SUPABASE_URL}}';
const publicUrl = data.success ? `${supabaseUrl}/storage/v1/object/public/media-assets/recordings/${data.channel_id || 'general'}/${data.job_id}.mp4` : null;

return [{
  json: {
    success: data.success,
    job_id: data.job_id,
