const config = $node['ğŸ“‹ APIæº–å‚™'].json;
const apiResult = $input.first().json;

let status = 'processing';
let outputUrl = null;
let errorMessage = null;
let predictionId = null;

// Replicate API ãƒ¬ã‚¹ãƒãƒ³ã‚¹è§£æ
if (config.api_type === 'replicate') {
  if (apiResult.error) {
    status = 'failed';
