  } else if (apiResult.status === 'failed') {
    status = 'failed';
    errorMessage = apiResult.error || 'Processing failed';
  } else if (apiResult.id) {
    predictionId = apiResult.id;
    status = 'processing';
  }
}
// Fal.ai ãƒ¬ã‚¹ãƒãƒ³ã‚¹è§£æ
else if (config.api_type === 'fal') {
  if (apiResult.error) {
    status = 'failed';
    errorMessage = apiResult.error;
  } else if (apiResult.video && apiResult.video.url) {
    status = 'completed';
    outputUrl = apiResult.video.url;
  } else if (apiResult.request_id) {
    predictionId = apiResult.request_id;
    status = 'processing';
  }
}

return [{
