const products = $node['è£œå®Œå¾…ã¡å–å¾—ï¼ˆuser_idæ¤œè¨¼ï¼‰'].json;
const productArr = Array.isArray(products) ? products : [products];
const configArr = Array.isArray($node['è¨ˆç®—è¨­å®šå–å¾—ï¼ˆuser_idæ¤œè¨¼ï¼‰'].json) ? $node['è¨ˆç®—è¨­å®šå–å¾—ï¼ˆuser_idæ¤œè¨¼ï¼‰'].json : [$node['è¨ˆç®—è¨­å®šå–å¾—ï¼ˆuser_idæ¤œè¨¼ï¼‰'].json];
const htsArr = Array.isArray($input.first().json) ? $input.first().json : [$input.first().json];

const config = { exchange_rate: 150, target_margin: 15, fvf_rate: 12.95, min_margin: 5, ddp_service_fee: 15 };
for (const row of configArr) {
