const htsMaster = htsArr.filter(h => h.hts_code);

function searchHTS(title, ebayCategory) {
  const titleLower = (title || '').toLowerCase();
  const categoryId = parseInt(ebayCategory) || null;
  if (categoryId) {
    const catMatch = htsMaster.find(h => h.ebay_category_ids && h.ebay_category_ids.includes(categoryId) && h.confidence_level === 'high');
    if (catMatch) return { ...catMatch, match_type: 'category_exact', confidence: 95 };
  }
  for (const h of htsMaster) {
    if (!h.category_keywords || h.category_keywords.length === 0) continue;
    const matchedKW = h.category_keywords.filter(kw => titleLower.includes(kw.toLowerCase()));
    if (matchedKW.length > 0) {
      const conf = Math.min(90, 70 + matchedKW.length * 5);
      return { ...h, match_type: 'keyword_match', matched_keywords: matchedKW, confidence: h.confidence_level === 'high' ? conf : conf - 10 };
    }
  }
  return null;
}

return productArr.filter(p => p.id).map(p => {
  let listingData = {}, manualOverride = {};
  try { listingData = typeof p.listing_data === 'string' ? JSON.parse(p.listing_data || '{}') : (p.listing_data || {}); } catch (e) {}
  try { manualOverride = typeof p.manual_override === 'string' ? JSON.parse(p.manual_override || '{}') : (p.manual_override || {}); } catch (e) {}

  const htsMatch = searchHTS(p.english_title || p.title, p.ebay_category_id);
