    finalHTS = { hts_code: htsMatch.hts_code, tariff_rate: htsMatch.base_tariff_rate || 0, source: 'master_match', confidence: htsMatch.confidence };
  } else if (p.hts_code) {
    finalHTS = { hts_code: p.hts_code, tariff_rate: 0.06, source: 'existing', confidence: 60 };
  }

