const p = $node['3ä»¶ãšã¤å‡¦ç†'].json;
const config = p.config || { target_margin: 15, fvf_rate: 12.95, ddp_service_fee: 15 };

// HTS/é‡é‡/åŸç”£å›½ã®æ±ºå®šï¼ˆæ‰‹å‹•å„ªå…ˆï¼‰
let htsCode = p.final_hts.hts_code, htsSource = p.final_hts.source, confidence = p.final_hts.confidence;
if (p.final_hts.source === 'pending_ai' && ai.hts_code) { htsCode = ai.hts_code; htsSource = 'ai_inference'; confidence = ai.confidence || 70; }
else if (!htsCode) { htsCode = '9504.40.00'; htsSource = 'default'; confidence = 50; }

let weightG = p.final_weight_g;
if (weightG === 500 && ai.weight_g) weightG = ai.weight_g;

let origin = p.final_origin;
if (!origin && ai.origin_country) origin = ai.origin_country;
