// DDPä¾¡æ ¼è¨ˆç®—
const exchangeRate = p.exchange_rate || 150;
const costUSD = (p.cost_jpy || 0) / exchangeRate;
const weightKg = weightG / 1000;
const targetMargin = (config.target_margin || 15) / 100;
const fvfRate = (config.fvf_rate || 12.95) / 100;

const TARIFF_RATES = { '9504.40': 0, '9503.00': 0, '8471.30': 0, '8517.12': 0 };
const htsPrefix = (htsCode || '').substring(0, 7);
let baseTariff = TARIFF_RATES[htsPrefix] ?? 0.06;
let additionalTariff = origin === 'CN' ? 0.25 : 0;
const totalTariffRate = baseTariff + additionalTariff;

const SHIPPING = [{ maxKg: 0.5, base: 12, total: 15 }, { maxKg: 1.0, base: 15, total: 20 }, { maxKg: 2.0, base: 20, total: 28 }, { maxKg: 5.0, base: 35, total: 45 }, { maxKg: 10.0, base: 55, total: 70 }];
const policy = SHIPPING.find(s => weightKg <= s.maxKg) || SHIPPING[SHIPPING.length - 1];
const baseShipping = policy.base, totalShipping = policy.total;

const variableRate = fvfRate + 0.02 + 0.03 + 0.015;
const DDP_FEE = config.ddp_service_fee || 15;
const insertionFee = 0.35;

let productPrice = 50;
for (let i = 0; i < 10; i++) {
  const tariff = productPrice * totalTariffRate;
  const salesTax = productPrice * 0.08;
  const mpf = productPrice * 0.003464;
  const ddpCost = tariff + salesTax + mpf + DDP_FEE;
  const fixedCost = costUSD + baseShipping + ddpCost + insertionFee;
  const requiredRevenue = fixedCost / (1 - targetMargin - variableRate);
  const newPrice = requiredRevenue - totalShipping;
  if (Math.abs(newPrice - productPrice) < 0.1) { productPrice = newPrice; break; }
  productPrice = newPrice;
}
productPrice = Math.max(10, Math.round(productPrice / 5) * 5);

if (p.sm_lowest_price > 0 && productPrice < p.sm_lowest_price * 0.8) productPrice = Math.round(p.sm_lowest_price * 0.9 / 5) * 5;
