const ebayFees = totalRevenue * (fvfRate + 0.02 + 0.03 + 0.015) + insertionFee;
const totalCosts = costUSD + baseShipping + ddpTotal + ebayFees;
const profit = totalRevenue - totalCosts;
const profitMargin = totalRevenue > 0 ? (profit / totalRevenue) * 100 : 0;

const hasTariff = totalTariffRate > 0;
const requiredMargin = hasTariff ? 10 : 5;
let isRedFlag = false, workflowStatus = 'ready', errorReason = '';
