else if (profit < 0) { workflowStatus = 'review'; isRedFlag = true; errorReason = 'èµ¤å­—:$' + profit.toFixed(2); }
else if (profitMargin < requiredMargin) { workflowStatus = 'review'; isRedFlag = true; errorReason = 'åˆ©ç›Šç‡ä¸è¶³:' + profitMargin.toFixed(1) + '%'; }

