// ========================================
// V4-PRODUCTION: ÂÜÖÈÉ®„Éà„Éº„ÇØ„É≥Ê§úË®º + „Éû„É´„ÉÅ„ÉÜ„Éä„É≥„Éà
// ========================================
const body = items[0].json.body || items[0].json;
const headers = items[0].json.headers || {};

// ÂÜÖÈÉ®„Éà„Éº„ÇØ„É≥Ê§úË®º
const internalToken = headers['x-n3-signature'] || headers['X-N3-Signature'];
const tokenTimestamp = headers['x-n3-token-timestamp'] || headers['X-N3-Token-Timestamp'];

if (internalToken && tokenTimestamp) {
  const crypto = /* crypto removed */;
  const secret = $env.N8N_INTERNAL_SECRET ;
  const userId = body.user_id || 'default';
  const expected = crypto.createHmac('sha256', secret).update(`${userId}-${tokenTimestamp}`).digest('hex');
  const isValid = internalToken === expected;
  const isNotExpired = (Date.now() - parseInt(tokenTimestamp)) < 300000;
  if (!isValid || !isNotExpired) {
    return [{ json: { error: true, message: 'ÂÜÖÈÉ®„Éà„Éº„ÇØ„É≥„ÅåÁÑ°Âäπ„Åß„Åô', code: 'INVALID_TOKEN' } }];
  }
}

const productId = body.product_id;
const userId = body.user_id || 'default';
const targetMarketplaces = body.target_marketplaces || ['ebay_us', 'qoo10_jp', 'shopee_sg', 'amazon_us'];
const targetMargin = body.target_margin || 15;

if (!productId) return [{ json: { error: true, message: 'product_id „ÅåÂøÖË¶Å„Åß„Åô' } }];

return [{ json: { 
  product_id: productId, 
  user_id: userId, 
  target_marketplaces: targetMarketplaces, 
  target_margin: targetMargin, 
  request_timestamp: new Date().toISOString() 
} }];

// ========================================
// V4-PRODUCTION: Â§öË≤©Ë∑Ø‰æ°Ê†ºË®àÁÆó„Ç®„É≥„Ç∏„É≥
// ========================================
// ÁâπÂæ¥:
// - ÁÇ∫Êõø„Éê„ÉÉ„Éï„Ç° 2% + „Çπ„Éó„É¨„ÉÉ„ÉâÂãïÁöÑÂèñÂæó
// - Ê±∫Ê∏àÂõ∫ÂÆöË≤ªÔºàË≤©Ë∑ØÂà•Ôºâ
// - ÂÆπÁ©çÈáçÈáèË®àÁÆó
// - ÁáÉÊ≤π„Çµ„Éº„ÉÅ„É£„Éº„Ç∏
// - Supabase 500MBÂØæÁ≠ñ: ÂúßÁ∏Æ‰øùÂ≠ò
// ========================================

const request = $node['„É™„ÇØ„Ç®„Çπ„ÉàÊ§úË®º + ÂÜÖÈÉ®„Éà„Éº„ÇØ„É≥Ê§úË®º'].json;
const productResult = $node['ÂïÜÂìÅÊÉÖÂ†±ÂèñÂæó'].json;
const feesResult = $node['ÊâãÊï∞Êñô„Éû„Çπ„ÇøÂèñÂæó'].json;
const exchangeResult = $node['ÁÇ∫Êõø„É¨„Éº„ÉàÂèñÂæó'].json;
const shippingResult = $node['ÈÄÅÊñô„Éû„Çπ„ÇøÂèñÂæóÔºàËªΩÈáèÁâàÔºâ'].json;

const product = Array.isArray(productResult) ? productResult[0] : productResult;
const fees = Array.isArray(feesResult) ? feesResult : [feesResult];
const rates = Array.isArray(exchangeResult) ? exchangeResult : [exchangeResult];
const shippingRates = Array.isArray(shippingResult) ? shippingResult : [shippingResult];

if (!product || !product.id) {
  return [{ json: { error: true, message: 'ÂïÜÂìÅ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì' } }];
}

// ========================================
// ÁÇ∫Êõø„É¨„Éº„ÉàÔºà2%„Éê„ÉÉ„Éï„Ç° + „Çπ„Éó„É¨„ÉÉ„ÉâÔºâ
// ========================================
const FX_BUFFER = 0.02; // 2%„Éê„ÉÉ„Éï„Ç°
const exchangeRates = {};
rates.forEach(r => {
  if (r && r.currency) {
    const spread = parseFloat(r.spread_percent || 0) / 100;
    exchangeRates[r.currency] = parseFloat(r.rate) * (1 + FX_BUFFER + spread);
  }
});
exchangeRates['JPY'] = 1;

// ========================================
// Ê±∫Ê∏àÂõ∫ÂÆöË≤ªÔºàË≤©Ë∑ØÂà•„ÉªÈÄöË≤®Âà• ‚Üí ÂÜÜÊèõÁÆóÔºâ
// ========================================
const PAYMENT_FIXED_FEES = {
  'ebay_us': 45,      // $0.30 √ó 150ÂÜÜ
  'ebay_uk': 57,      // ¬£0.30 √ó 190ÂÜÜ
  'ebay_de': 42,      // ‚Ç¨0.35 √ó 120ÂÜÜ
  'ebay_au': 30,      // A$0.30 √ó 100ÂÜÜ
  'amazon_us': 153,   // $0.99 √ó 155ÂÜÜ
  'amazon_uk': 114,   // ¬£0.60 √ó 190ÂÜÜ
  'shopee_sg': 58,    // S$0.50 √ó 115ÂÜÜ
  'shopee_my': 35,    // RM0.50 √ó 70ÂÜÜ
  'lazada_sg': 50,
  'lazada_my': 30,
  'qoo10_jp': 0       // Âõ∫ÂÆöË≤ª„Å™„Åó
};

// ========================================
// ÊâãÊï∞Êñô„Éû„ÉÉ„Éó
// ========================================
const feeMap = {};
fees.forEach(f => {
  if (f && f.marketplace_id) {
    feeMap[f.marketplace_id] = {
      platform: parseFloat(f.platform_fee_percent || 12) / 100,
      payment: parseFloat(f.payment_fee_percent || 3) / 100,
      vat: parseFloat(f.vat_rate || 0) / 100,
      currency: f.currency || 'USD'
    };
  }
});

// ========================================
// ÈÄÅÊñôË®àÁÆóÔºàÂÆπÁ©çÈáçÈáèÂØæÂøúÔºâ
// ========================================
const marketplaceToCountry = {
  'ebay_us': 'US', 'ebay_uk': 'GB', 'ebay_de': 'DE', 'ebay_au': 'AU',
  'amazon_us': 'US', 'amazon_uk': 'GB', 'amazon_de': 'DE',
  'shopee_sg': 'SG', 'shopee_my': 'MY', 'shopee_th': 'TH',
  'lazada_sg': 'SG', 'lazada_my': 'MY',
  'qoo10_jp': 'JP'
};

const actualWeight = product.weight_grams || 500;
const L = product.dimension_cm_l || 20;
const W = product.dimension_cm_w || 15;
const H = product.dimension_cm_h || 10;
const volumetricWeight = Math.ceil((L * W * H) / 5000 * 1000); // cm¬≥ ‚Üí grams
const chargeableWeight = Math.max(actualWeight, volumetricWeight);

function getShippingCost(country) {
  const countryRates = shippingRates.filter(r => r && r.destination_country === country);
  const applicable = countryRates.find(r => 
    chargeableWeight >= r.weight_min_grams && 
    chargeableWeight <= r.weight_max_grams
  );
  if (!applicable) {
    const maxRate = countryRates.sort((a, b) => b.weight_max_grams - a.weight_max_grams)[0];
    if (maxRate) {
      const surcharge = parseFloat(maxRate.fuel_surcharge_rate || 12) / 100;
      return Math.ceil(maxRate.price_jpy * (1 + surcharge));
    }
    return 2500; // „Éá„Éï„Ç©„É´„Éà
  }
  const surcharge = parseFloat(applicable.fuel_surcharge_rate || 12) / 100;
  return Math.ceil(applicable.price_jpy * (1 + surcharge));
}

// ========================================
// ÂêÑË≤©Ë∑Ø„ÅÆ‰æ°Ê†ºË®àÁÆó
// ========================================
const purchasePrice = parseFloat(product.purchase_price || 0);
const packagingCost = parseFloat(product.packaging_cost || 200);
const targetMargin = request.target_margin / 100;

const calculations = [];

request.target_marketplaces.forEach(marketplace => {
  const country = marketplaceToCountry[marketplace] || 'US';
  const fee = feeMap[marketplace] || { platform: 0.12, payment: 0.03, vat: 0, currency: 'USD' };
  const currency = fee.currency;
  const exchangeRate = exchangeRates[currency] || 150;

  const shippingCostJPY = country === 'JP' ? 0 : getShippingCost(country);
  const paymentFixedJPY = PAYMENT_FIXED_FEES[marketplace] || 0;

  // Âéü‰æ°ÂêàË®àÔºàÂÜÜÔºâ
  const totalCostJPY = purchasePrice + shippingCostJPY + packagingCost + paymentFixedJPY;

  // ÊâãÊï∞ÊñôÁéáÂêàË®à
  const totalFeeRate = fee.platform + fee.payment + fee.vat;

  // Âà©ÁõäÈÄÜÁÆóÂºè: Ë≤©Â£≤‰æ°Ê†º = (Âéü‰æ° √ó (1 + ÁõÆÊ®ôÂà©ÁõäÁéá)) / (1 - ÊâãÊï∞ÊñôÁéá)
  const sellingPriceJPY = Math.ceil((totalCostJPY * (1 + targetMargin)) / (1 - totalFeeRate));

  // Â§ñË≤®ÊèõÁÆóÔºàÂàá„Çä‰∏ä„ÅíÔºâ
  const sellingPriceForeign = Math.ceil((sellingPriceJPY / exchangeRate) * 100) / 100;

  // ÂÆüÈöõ„ÅÆÂà©ÁõäË®àÁÆó
  const revenueJPY = sellingPriceForeign * exchangeRate;
  const feeJPY = Math.ceil(revenueJPY * totalFeeRate);
  const profitJPY = Math.floor(revenueJPY - totalCostJPY - feeJPY);
  const profitRate = totalCostJPY > 0 ? Math.round((profitJPY / totalCostJPY) * 1000) / 10 : 0;
  const isProfitable = profitJPY > 0 && profitRate >= 5;

  calculations.push({
    marketplace: marketplace,
    selling_price: sellingPriceForeign,
    selling_price_jpy: sellingPriceJPY,
    currency: currency,
    cost_breakdown: {
      purchase: purchasePrice,
      shipping: shippingCostJPY,
      packaging: packagingCost,
      payment_fixed: paymentFixedJPY,
      total: totalCostJPY
    },
    fee_breakdown: {
      platform_rate: fee.platform,
      payment_rate: fee.payment,
      vat_rate: fee.vat,
      total_rate: totalFeeRate,
      total_fee_jpy: feeJPY
    },
    exchange_rate: exchangeRate,
    exchange_rate_with_buffer: `${(FX_BUFFER * 100).toFixed(1)}%`,
    profit_jpy: profitJPY,
    profit_rate: profitRate,
    is_profitable: isProfitable ? 1 : 0,
    chargeable_weight: chargeableWeight,
    actual_weight: actualWeight,
    volumetric_weight: volumetricWeight
  });
});

// ========================================
// Supabase 500MBÂØæÁ≠ñ: ÂúßÁ∏ÆÁâà„Éá„Éº„ÇøÁîüÊàê
// DB‰øùÂ≠òÁî®ÔºàÁ¥Ñ85%ÂâäÊ∏õÔºâ
// ========================================
const compressed_for_db = calculations.map(c => ({
  m: c.marketplace,           // marketplace
  p: c.selling_price,         // selling_price
  pj: c.selling_price_jpy,    // selling_price_jpy
  c: c.currency,              // currency
  pr: c.profit_jpy,           // profit_jpy
  prc: c.profit_rate,         // profit_rate
  ok: c.is_profitable,        // is_profitable
  fx: Math.round(c.exchange_rate * 100) / 100  // exchange_rateÔºàÂ∞èÊï∞ÁÇπ2Ê°ÅÔºâ
}));

return [{ json: {
  product_id: product.id,
  product_updated_at: product.updated_at,
  sku: product.sku,
  user_id: request.user_id,
  target_margin: request.target_margin,
  calculations: calculations,
  compressed_for_db: compressed_for_db,
  calculated_at: new Date().toISOString()
} }];

// ÂÆåÂÖ®Áâà„Éá„Éº„Çø„ÇíAPI„É¨„Çπ„Éù„É≥„Çπ„Å®„Åó„Å¶Ëøî„Åô
const calcResult = $node['Â§öË≤©Ë∑Ø‰æ°Ê†ºË®àÁÆó„Ç®„É≥„Ç∏„É≥'].json;

return [{ json: {
  success: true,
  product_id: calcResult.product_id,
  sku: calcResult.sku,
  target_margin: calcResult.target_margin,
  calculations: calcResult.calculations, // ÂÆåÂÖ®ÁâàÔºàAPIÂøúÁ≠îÁî®Ôºâ
  calculated_at: calcResult.calculated_at,
  storage_note: 'DB‰øùÂ≠ò„ÅØÂúßÁ∏ÆÁâàÔºàÁ¥Ñ85%ÂâäÊ∏õÔºâ'
} }];

