// üîê HMAC SHA256 ÁΩ≤ÂêçÊ§úË®º (Empire OS v6)
const crypto = require('crypto');

const signature = $request.headers['x-n3-signature'] || $request.headers['X-N3-Signature'];
const timestamp = $request.headers['x-n3-timestamp'] || $request.headers['X-N3-Timestamp'];
const secret = $env.N8N_HMAC_SECRET;

if (!secret) {
  throw new Error('üö´ N8N_HMAC_SECRET Áí∞Â¢ÉÂ§âÊï∞„ÅåÊú™Ë®≠ÂÆö„Åß„Åô');
}

if (!signature || !timestamp) {
  throw new Error('üö´ ÁΩ≤Âêç„Åæ„Åü„ÅØ„Çø„Ç§„É†„Çπ„Çø„É≥„Éó„ÅåÊ¨†ËêΩ„Åó„Å¶„ÅÑ„Åæ„Åô');
}

// „Çø„Ç§„É†„Çπ„Çø„É≥„ÉóÊúâÂäπÊúüÈôê„ÉÅ„Çß„ÉÉ„ÇØ (5ÂàÜ)
const now = Date.now();
const tsAge = now - parseInt(timestamp);
if (tsAge > 300000 || tsAge < -30000) {
  throw new Error('üö´ „Çø„Ç§„É†„Çπ„Çø„É≥„Éó„ÅåÊúüÈôêÂàá„Çå„Åæ„Åü„ÅØ‰∏çÊ≠£„Åß„Åô');
}

// HMACÊ§úË®º
const bodyString = JSON.stringify($json);
const computedHmac = crypto
  .createHmac('sha256', secret)
  .update(timestamp + '.' + bodyString)
  .digest('hex');

if (signature !== computedHmac) {
  throw new Error('üö´ HMACÁΩ≤ÂêçÊ§úË®ºÂ§±Êïó: ‰∏çÊ≠£„Å™„É™„ÇØ„Ç®„Çπ„Éà');
}

// Ê§úË®ºÊàêÂäü - ÂÖÉ„Éá„Éº„Çø„Çí„Åù„ÅÆ„Åæ„ÅæËøî„Åô
return [{ json: $json }];
