const body = $input.first().json.body || $input.first().json || {};
const secret = $env.N8N_INTERNAL_SECRET;
if (!secret) return [{ json: { error: true, message: 'N8N_INTERNAL_SECRETÊú™Ë®≠ÂÆö', code: 'CONFIG_ERROR' } }];
const sourceCountry = body.source_country || 'US';
const destCountry = body.dest_country || 'JP';
const productCost = parseFloat(body.product_cost) || 0;
const sourceCurrency = body.source_currency || 'USD';
const weightKg = parseFloat(body.weight_kg) || 0.5;
const needsInspection = body.needs_inspection || false;
const needsPhoto = body.needs_photo || false;
const htsCode = body.hts_code || '';
if (productCost <= 0) return [{ json: { error: true, message: 'product_cost „ÅØÂøÖÈ†à', code: 'MISSING_PARAM' } }];
return [{ json: { source_country: sourceCountry, dest_country: destCountry, product_cost: productCost, source_currency: sourceCurrency, weight_kg: weightKg, needs_inspection: needsInspection, needs_photo: needsPhoto, hts_code: htsCode } }];

const config = $node['üîê ÂàùÊúüÂåñ'].json;
const settingsRaw = $node['üìä „Ç∞„É≠„Éº„Éê„É´Ë®≠ÂÆöÂèñÂæó'].json;
const providersRaw = $node['üöö ÈÖçÈÄÅ‰ª£Ë°åÂèñÂæó'].json;

// Ë®≠ÂÆö„Çí„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„Å´Â§âÊèõ
const settings = {};
const settingsArray = Array.isArray(settingsRaw) ? settingsRaw : [settingsRaw];
settingsArray.forEach(s => { if (s && s.setting_key) settings[s.setting_key] = parseFloat(s.setting_value) || s.setting_value; });

// ÁÇ∫Êõø„É¨„Éº„ÉàÂèñÂæó
const getExchangeRate = (from, to) => {
  if (from === to) return 1;
  const key = `exchange_rate_${from.toLowerCase()}_${to.toLowerCase()}`;
  if (settings[key]) return settings[key];
  // ÈÄÜ„É¨„Éº„Éà
  const reverseKey = `exchange_rate_${to.toLowerCase()}_${from.toLowerCase()}`;
  if (settings[reverseKey]) return 1 / settings[reverseKey];
  // JPYÁµåÁî±
  const toJpy = settings[`exchange_rate_${from.toLowerCase()}_jpy`] || 1;
  const fromJpy = settings[`exchange_rate_${to.toLowerCase()}_jpy`] || 1;
  return toJpy / fromJpy;
};

// VATÁéáÂèñÂæó
const getVatRate = (country) => {
  const vatMap = { 'DE': 0.19, 'FR': 0.20, 'UK': 0.20, 'GB': 0.20, 'IT': 0.22, 'ES': 0.21, 'EU': 0.21 };
  const key = `vat_rate_${country.toLowerCase()}`;
  return settings[key] || vatMap[country] || 0;
};

// ÈÖçÈÄÅ‰ª£Ë°åÊ•≠ËÄÖÔºà„Éï„Ç©„Éº„É´„Éê„ÉÉ„ÇØÔºâ
const providers = Array.isArray(providersRaw) ? providersRaw : (providersRaw ? [providersRaw] : []);
const defaultProviders = [
  { name: 'Standard Forwarding', handling_fee: 5, inspection_fee: 2, photo_fee: 1, base_shipping: 15, per_kg: 8 },
  { name: 'Express Forwarding', handling_fee: 8, inspection_fee: 3, photo_fee: 2, base_shipping: 25, per_kg: 12 }
];
const activeProviders = providers.length > 0 ? providers : defaultProviders;

// Ë®àÁÆó
const exchangeRate = getExchangeRate(config.source_currency, 'JPY');
const productCostJpy = config.product_cost * exchangeRate;
const vatRate = getVatRate(config.dest_country);

const calculations = activeProviders.map(provider => {
  const handlingFee = parseFloat(provider.handling_fee) || 5;
  const inspectionFee = config.needs_inspection ? (parseFloat(provider.inspection_fee) || 2) : 0;
  const photoFee = config.needs_photo ? (parseFloat(provider.photo_fee) || 1) : 0;
  const baseShipping = parseFloat(provider.base_shipping) || 15;
  const perKg = parseFloat(provider.per_kg) || 8;
  const shippingCost = baseShipping + (config.weight_kg * perKg);
  const forwardingTotal = handlingFee + inspectionFee + photoFee + shippingCost;
  const dutyRate = 0.05; // Á∞°ÊòìÈñ¢Á®éÁéá
  const dutyAmount = productCostJpy * dutyRate;
  const subtotal = productCostJpy + (forwardingTotal * exchangeRate) + dutyAmount;
  const vatAmount = subtotal * vatRate;
  const totalCostJpy = subtotal + vatAmount;
  return {
    provider_name: provider.name,
    handling_fee: handlingFee,
    inspection_fee: inspectionFee,
    photo_fee: photoFee,
    shipping_cost: Math.round(shippingCost * 100) / 100,
    forwarding_total_usd: Math.round(forwardingTotal * 100) / 100,
    duty_amount_jpy: Math.round(dutyAmount),
    vat_amount_jpy: Math.round(vatAmount),
    total_cost_jpy: Math.round(totalCostJpy),
    total_cost_usd: Math.round(totalCostJpy / exchangeRate * 100) / 100
  };
});

// ÊúÄÂÆâÂÄ§„ÇíÁâπÂÆö
const cheapest = calculations.reduce((min, c) => c.total_cost_jpy < min.total_cost_jpy ? c : min, calculations[0]);

return [{ json: { success: true, input: config, exchange_rate: exchangeRate, vat_rate: vatRate, product_cost_jpy: Math.round(productCostJpy), calculations: calculations, recommended: cheapest } }];

// üîê HMAC SHA256 ÁΩ≤ÂêçÊ§úË®º (Empire OS v6)
const crypto = require('crypto');

const signature = $request.headers['x-n3-signature'] || $request.headers['X-N3-Signature'];
const timestamp = $request.headers['x-n3-timestamp'] || $request.headers['X-N3-Timestamp'];
const secret = $env.N8N_HMAC_SECRET;

if (!secret) {
  throw new Error('üö´ N8N_HMAC_SECRET Áí∞Â¢ÉÂ§âÊï∞„ÅåÊú™Ë®≠ÂÆö„Åß„Åô');
}

if (!signature || !timestamp) {
  throw new Error('üö´ ÁΩ≤Âêç„Åæ„Åü„ÅØ„Çø„Ç§„É†„Çπ„Çø„É≥„Éó„ÅåÊ¨†ËêΩ„Åó„Å¶„ÅÑ„Åæ„Åô');
}

// „Çø„Ç§„É†„Çπ„Çø„É≥„ÉóÊúâÂäπÊúüÈôê„ÉÅ„Çß„ÉÉ„ÇØ (5ÂàÜ)
const now = Date.now();
const tsAge = now - parseInt(timestamp);
if (tsAge > 300000 || tsAge < -30000) {
  throw new Error('üö´ „Çø„Ç§„É†„Çπ„Çø„É≥„Éó„ÅåÊúüÈôêÂàá„Çå„Åæ„Åü„ÅØ‰∏çÊ≠£„Åß„Åô');
}

// HMACÊ§úË®º
const bodyString = JSON.stringify($json);
const computedHmac = crypto
  .createHmac('sha256', secret)
  .update(timestamp + '.' + bodyString)
  .digest('hex');

if (signature !== computedHmac) {
  throw new Error('üö´ HMACÁΩ≤ÂêçÊ§úË®ºÂ§±Êïó: ‰∏çÊ≠£„Å™„É™„ÇØ„Ç®„Çπ„Éà');
}

// Ê§úË®ºÊàêÂäü - ÂÖÉ„Éá„Éº„Çø„Çí„Åù„ÅÆ„Åæ„ÅæËøî„Åô
return [{ json: $json }];
