// Auth-Gate V8.2.1 - JITトークン検証
const input = $input.first().json;
const startTime = Date.now();

// リクエストヘッダーからトークン抽出
const authHeader = input.headers?.authorization || input.headers?.Authorization || '';
const token = authHeader.replace('Bearer ', '').trim();
const workflowName = input.workflowName || 'unknown';
const requestId = input.requestId || `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
const tenantId = input.tenantId || 'default';
const sourceIp = input.headers?.['x-forwarded-for'] || input.headers?.['x-real-ip'] || 'unknown';
const userAgent = input.headers?.['user-agent'] || 'unknown';

// 検証結果
let validationResult = {
  isValid: false,
  reason: '',
  tenantId: tenantId,
  requestId: requestId,
  workflowName: workflowName,
  tokenProvided: token ? token.substring(0, 8) + '***' : 'none',
  sourceIp: sourceIp,
  userAgent: userAgent,
  validatedAt: new Date().toISOString()
};

// トークン存在チェック
if (!token) {
  validationResult.reason = 'missing_token';
  validationResult.errorMessage = 'Authorization token is required';
  return {
    json: {
      ...validationResult,
      shouldLogUnauthorized: true,
      originalPayload: input
    }
  };
}

// トークンフォーマットチェック（基本検証）
if (token.length < 32) {
  validationResult.reason = 'invalid_token_format';
  validationResult.errorMessage = 'Invalid token format';
  return {
    json: {
      ...validationResult,
      shouldLogUnauthorized: true,
      originalPayload: input
    }
  };
}

// SHA256ハッシュ生成（DB検証用）
const crypto = require('crypto');
const tokenHash = crypto.createHash('sha256').update(token).digest('hex');

validationResult.tokenHash = tokenHash;
validationResult.validationTimeMs = Date.now() - startTime;

return {
  json: {
    ...validationResult,
    needsDbValidation: true,
    originalPayload: input
  }
};

// DB検証結果の処理
const input = $input.first().json;
const dbResult = input.result || {};
const prevData = $('Token Validation').first().json;

let finalResult = {
  ...prevData,
  dbValidation: dbResult
};

if (dbResult.valid === true) {
  finalResult.isValid = true;
  finalResult.reason = 'token_valid';
  finalResult.tenantId = dbResult.tenant_id || prevData.tenantId;
  finalResult.requestId = dbResult.request_id || prevData.requestId;
} else {
  finalResult.isValid = false;
  finalResult.reason = dbResult.reason || 'db_validation_failed';
  finalResult.errorMessage = dbResult.message || 'Token validation failed';
  finalResult.shouldLogUnauthorized = true;
}

return { json: finalResult };

// 認証成功 - 元のペイロードにメタデータを追加して返す
const input = $input.first().json;

return {
  json: {
    authValidated: true,
    tenantId: input.tenantId,
    requestId: input.requestId,
    workflowName: input.workflowName,
    validatedAt: input.validatedAt,
    originalPayload: input.originalPayload,
    ...input.originalPayload  // 元のリクエストデータを展開
  }
};

// 認証失敗 - エラーレスポンスを返す
const input = $input.first().json;

return {
  json: {
    authValidated: false,
    error: true,
    errorCode: 'AUTH_GATE_BLOCKED',
    errorMessage: input.errorMessage || 'Authentication failed',
    reason: input.reason,
    requestId: input.requestId,
    workflowName: input.workflowName,
    timestamp: new Date().toISOString()
  }
};
