const data = $node['ðŸ“‹ ã‚¹ã‚­ãƒ£ãƒ³æº–å‚™'].json;
const exifResult = $input.first().json.stdout || '{}';

let detectedMetadata = {};
try {
  detectedMetadata = JSON.parse(exifResult);
  if (Array.isArray(detectedMetadata)) detectedMetadata = detectedMetadata[0] || {};
} catch (e) {
  detectedMetadata = { parse_error: exifResult };
}

// AIç”Ÿæˆã®ç—•è·¡ã‚’æ¤œå‡º
const aiIndicators = [];
