const outputFile = `/tmp/purified_${data.job_id}.${data.media_type === 'video' ? 'mp4' : data.media_type === 'audio' ? 'mp3' : 'jpg'}`;

let purifyCommands = [];

if (data.media_type === 'video') {
  // å‹•ç”»ã®å®Œå…¨å†ã‚¨ãƒ³ã‚³ãƒ¼ãƒ‰ï¼ˆå…¨ãƒ¡ã‚¿ãƒ‡ãƒ¼ã‚¿å‰Šé™¤ï¼‰
  const ffmpegCmd = [
    'ffmpeg -y',
    `-i "${data.input_file}"`,
    '-map_metadata -1', // å…¨ãƒ¡ã‚¿ãƒ‡ãƒ¼ã‚¿å‰Šé™¤
    '-fflags +bitexact', // ãƒ“ãƒƒãƒˆå˜ä½ã§ç•°ãªã‚‹å‡ºåŠ›
    '-flags:v +bitexact',
