    '-strip', // å…¨ãƒ¡ã‚¿ãƒ‡ãƒ¼ã‚¿å‰Šé™¤
    '-quality 95',
    '+noise Gaussian -evaluate Multiply 0.999', // å¾®ç´°ãƒŽã‚¤ã‚º
    '-modulate 100.5,100.5,100', // å¾®ç´°ãªè‰²èª¿å¤‰æ›´
    `"${outputFile}"`
  ].join(' ');

  purifyCommands.push(convertCmd);
  purifyCommands.push(`exiftool -all= -overwrite_original "${outputFile}"`);

} else if (data.media_type === 'audio') {
  // éŸ³å£°ã®å†ã‚¨ãƒ³ã‚³ãƒ¼ãƒ‰
  const ffmpegCmd = [
