// é‡ã¿ä»˜ããƒ©ãƒ³ãƒ€ãƒ é¸æŠž
const weightedRandom = (items) => {
  const totalWeight = items.reduce((sum, item) => sum + (item.weight || 1), 0);
  let random = Math.random() * totalWeight;
  for (const item of items) {
    random -= (item.weight || 1);
    if (random <= 0) return item;
  }
  return items[0];
};

// å„ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã®ã‚¢ã‚¯ã‚·ãƒ§ãƒ³ãƒ—ãƒ©ãƒ³ç”Ÿæˆ
const plans = [];

for (const account of accounts) {
  const accountPlan = {
    account_id: account.id,
    channel_id: account.youtube_channel_id,
    profile_id: account.profile_id,
    antidetect_provider: account.antidetect_provider,
    proxy_config: account.proxy_host ? {
      host: account.proxy_host,
      port: account.proxy_port,
      username: account.proxy_username,
      password: account.proxy_password
    } : null,
    actions: []
  };

  // ã‚¿ãƒ¼ã‚²ãƒƒãƒˆã‚¢ã‚¯ã‚·ãƒ§ãƒ³æ•°åˆ†ã®ãƒ—ãƒ©ãƒ³ã‚’ç”Ÿæˆ
  let currentDelay = 0;
  for (let i = 0; i < config.target_actions; i++) {
    const template = weightedRandom(templates);

    // ãƒ©ãƒ³ãƒ€ãƒ ãªé…å»¶ï¼ˆ30ç§’ã€œ3åˆ†ï¼‰
    const delaySeconds = 30 + Math.floor(Math.random() * 150);
    currentDelay += delaySeconds;

    // è¦–è´æ™‚é–“ã®ãƒ©ãƒ³ãƒ€ãƒ åŒ–
    let duration = 0;
    if (template.action_type === 'watch') {
      duration = template.min_duration_seconds + 
        Math.floor(Math.random() * (template.max_duration_seconds - template.min_duration_seconds));
    }

    // ã‚³ãƒ¡ãƒ³ãƒˆã®ãƒ©ãƒ³ãƒ€ãƒ é¸æŠž
    let comment = null;
    if (template.action_type === 'comment' && template.comment_templates) {
      const comments = typeof template.comment_templates === 'string' 
        ? JSON.parse(template.comment_templates) 
        : template.comment_templates;
      if (comments.length > 0) {
        comment = comments[Math.floor(Math.random() * comments.length)];
      }
    }

    accountPlan.actions.push({
      sequence: i + 1,
      action_type: template.action_type,
      target_category: template.target_category,
      delay_seconds: delaySeconds,
      cumulative_delay: currentDelay,
      duration_seconds: duration,
      comment_text: comment
    });
  }

  plans.push(accountPlan);
}

return plans.map(p => ({ json: p }));

