// Puppeteerã‚¹ã‚¯ãƒªãƒ—ãƒˆç”Ÿæˆï¼ˆç°¡ç•¥ç‰ˆï¼‰
function generatePuppeteerScript(actions) {
  return `
// Auto-generated warming script
const puppeteer = require('puppeteer-core');

async function warmAccount(wsEndpoint) {
  const browser = await puppeteer.connect({ browserWSEndpoint: wsEndpoint });
  const page = await browser.newPage();

  ${actions.map((a, i) => `
  // Action ${i + 1}: ${a.action_type}
  await new Promise(r => setTimeout(r, ${a.delay_seconds * 1000}));
  await page.goto('${a.youtube_url}');
  ${a.action_type === 'watch' ? `await new Promise(r => setTimeout(r, ${a.duration_seconds * 1000}));` : ''}
  ${a.action_type === 'like' ? `await page.click('button[aria-label*="like"]').catch(() => {});` : ''}
  ${a.action_type === 'comment' && a.comment_text ? `
  await page.click('#comment-placeholder-area').catch(() => {});
  await page.type('#contenteditable-root', '${a.comment_text}');
  await page.click('#submit-button').catch(() => {});
  ` : ''}
  `).join('')}

  await browser.disconnect();
}

module.exports = { warmAccount };
  `.trim();
}

